mapper.select:
SELECT 

FROM zlmt_resource_worksheet a

WHERE a.id = #{id}

SELECT 

FROM zlmt_resource_worksheet a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM zlmt_resource_worksheet a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE zlmt_resource_worksheet SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
